pragma solidity ^0.8.20;

import "./IERC20Mintable.sol";

enum RebaseYieldMode {
  AUTOMATIC,
  VOID,
  CLAIMABLE
}

interface IERC20Rebasing is IERC20Mintable {
  // changes the yield mode of the caller and update the balance
  // to reflect the configuration
  function configure(RebaseYieldMode) external returns (uint256);

  // "claimable" yield mode accounts can call this this claim their yield
  // to another address
  function claim(address recipient, uint256 amount) external returns (uint256);

  // read the claimable amount for an account
  function getClaimableAmount(address account) external view returns (uint256);
}
","keccak256":"0xf7a4a255bc133fb7a28e193d933d2e39e8153d281d8fb6cbef599b45ee41dc3b","license":"MIT"},"contracts/interface/ILockedStaking.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface ILockedStaking {
  error NotPoolAdmin(address user);
  error InvalidArguments();
  error TooManyStake();
  error PoolClosed(uint256 id);
  error PoolNotExisted(uint256 id);
  error NoStaking();
  error Locked(uint256 until);
  error NotStaker(address user);
  error InsufficientAmount(uint256 required);
  error ExceededMaxDuration();

  struct LockedPoolInfo {
    uint256 timelock;
    uint256 yieldAPY;
    address yieldToken;
    address stakeToken;
    uint256 totalStaked;
    uint256 nextStakeId;
    bool enabled;
  }

  struct StakingInfo {
    address user;
    uint256 amount;
    uint256 stakeTime;
    uint256 unlockTime;
    uint256 yieldAmount;
    uint256 yieldDebt; // used for calculate generated yield
  }

  event PoolCreated(
    uint256 id,
    uint256 timelock,
    uint256 yieldAPY,
    address yieldToken,
    address stakeToken
  );

  event Stake(
    uint256 poolId,
    uint256 stakeId,
    uint256 timelock,
    uint256 yield,
    address yieldToken,
    address stakeToken,
    uint256 amount
  );

  event Unstake(
    uint256 poolId,
    uint256 stakeId,
    address stakeToken,
    uint256 totalAmount
  );

  event Extend(
    uint256 poolId,
    uint256 stakeId,
    uint256 duration
  );

  event Repay(
    uint256 poolId,
    uint256 stakeId,
    address token,
    uint256 totalStaked,
    uint256 repayAmount
  );

  function stake(uint256 poolId, uint256 amount) external payable;

  function unstake(uint256 poolId, uint256 stakeId) external;

  function getGeneratedYield(
    uint256 poolId,
    uint256 stakeId
  ) external view returns (uint256);

  function repayWithStakeToken(
    uint256 poolId,
    uint256 stakeId
  ) external payable;

  function repayWithYieldToken(
    uint256 poolId,
    uint256 stakeId
  ) external;
}
