pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract IDOPool is Ownable {
  error ExceededMaxStake();

  IERC20 public token;
  string public name;
  bool public lock;

  mapping(address => uint256) public stakedAmount;
  uint256 public totalStaked;
  uint256 public maxStaked = 1000 * 10**18;

  modifier locked() {
    require(lock, "Not Locked");
    _;
  }

  modifier notLocked() {
    require(!lock, "Locked");
    _;
  }

  constructor(address token_, string memory name_) Ownable(msg.sender) {
    token = IERC20(token_);
    name = name_;
    lock = true;
  }

  function setToken(address _token) external onlyOwner {
    token = IERC20(_token);
  }

  function setMaxStaked(uint256 _max) external onlyOwner {
    maxStaked = _max;
  }

  function setLock(bool _lock) external onlyOwner {
    lock = _lock;
  }

  function stake(uint256 amount) external locked {
    stakedAmount[msg.sender] = stakedAmount[msg.sender] + amount;
    totalStaked = totalStaked + amount;
    if (totalStaked > maxStaked) revert ExceededMaxStake(); 

    token.transferFrom(msg.sender, address(this), amount);
  }

  function unstake() external notLocked {
    uint256 staked = stakedAmount[msg.sender];
    stakedAmount[msg.sender] = 0;

    token.transfer(msg.sender, staked);
  }
}
