contracts/interface/ILockedStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface ILockedStaking {
  error NotPoolAdmin(address user);
  error InvalidArguments();
  error InvalidTokenAmounts(uint256 amount);
  error PoolClosed(uint256 id);
  error PoolNotExisted(uint256 id);
  error NoStaking();

  struct LockedPoolInfo {
    uint256 timelock;
    uint256 yieldAPY;
    address yieldToken;
    address stakeToken;
    uint256 totalStaked;
    bool enabled;
  }

  struct StakingInfo {
    uint256 amount;
    uint256 unlockTime;
    uint256 yieldAmount;
  }

  event PoolCreated(
    uint256 id,
    uint256 timelock,
    uint256 yieldAPY,
    address yieldToken,
    address stakeToken
  );

  event Stake(
    uint256 poolId,
    uint256 timelock,
    uint256 yield,
    address yieldToken,
    address stakeToken,
    uint256 amount
  );

  event Unstake(
    uint256 poolId,
    address stakeToken,
    uint256 totalAmount
  );

  function stake(uint256 poolId, uint256 amount) external payable;

  function unstake(uint256 poolId) external;
}

