contracts/LockedStakingPools.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./interface/IRoleControl.sol";
import "./interface/IERC20Mintable.sol";
import "./interface/ILockedStaking.sol";
import "./interface/IBlast.sol";
import "hardhat/console.sol";

contract LockedStakingPools is Initializable, ILockedStaking {
  using SafeERC20 for IERC20;

  uint256 public constant YIELD_DENOM = 100000;
  bytes32 public constant POOL_ADMIN_ROLE = keccak256("POOL_ADMIN_ROLE");

  IRoleControl roleControl;
  address treasury;
  uint256 nextPoolId = 1;
  mapping(uint256 => LockedPoolInfo) public poolInfo;
  mapping(uint256 => mapping(address => StakingInfo[])) stakeInfo;
  mapping(address => address) public yieldTokens;

  function init(address roleControl_, address treasury_) external initializer {
    roleControl = IRoleControl(roleControl_);
    if (treasury_ != address(0)) {
      IBlast(0x4300000000000000000000000000000000000002)
        .configureClaimableYield();
    }
  }

  modifier onlyPoolAdmin() {
    if (!roleControl.hasRole(POOL_ADMIN_ROLE, msg.sender))
      revert NotPoolAdmin(msg.sender);
    _;
  }

  function setTreasury(address _treasury) external onlyPoolAdmin {
    treasury = _treasury;
  }

  function claimNativeYield() external onlyPoolAdmin {
    IBlast(0x4300000000000000000000000000000000000002).claimAllYield(
      address(this),
      treasury
    );
  }

  function addSupportYieldTokens(
    address stakeToken,
    address yieldToken
  ) external onlyPoolAdmin {
    yieldTokens[stakeToken] = yieldToken;
  }

  function addLockedPools(
    uint256 timelock,
    uint256 yieldAPY,
    address stakeToken
  ) external onlyPoolAdmin returns (uint256 poolId) {
    if (timelock == 0 || yieldAPY == 0) revert InvalidArguments();
    address yieldToken = yieldTokens[stakeToken];
    if (yieldToken == address(0)) revert InvalidArguments();

    poolId = nextPoolId;
    poolInfo[poolId] = LockedPoolInfo(
      timelock,
      yieldAPY,
      yieldToken,
      stakeToken,
      0,
      true
    );

    nextPoolId = poolId + 1;

    emit PoolCreated(poolId, timelock, yieldAPY, yieldToken, stakeToken);
  }

  function closePool(uint256 poolId) external onlyPoolAdmin {
    poolInfo[poolId].enabled = false;
  }

  function setPoolYield(uint256 poolId, uint256 yield) external onlyPoolAdmin {
    if (poolId == 0 || yield == 0) revert InvalidArguments();
    if (poolInfo[poolId].timelock == 0) revert PoolNotExisted(poolId);

    poolInfo[poolId].yieldAPY = yield;
  }

  function _depositToken(address token, address from, uint256 amount) internal {
    if (token == address(0)) {
      if (amount != msg.value) revert InvalidTokenAmounts(amount);
    } else {
      IERC20(token).safeTransferFrom(from, address(this), amount);
    }
  }

  function _transferToken(address token, address to, uint256 amount) internal {
    if (token == address(0)) {
      if (address(this).balance < amount) revert InvalidTokenAmounts(amount);
      payable(to).transfer(amount);
    } else {
      IERC20(token).safeTransfer(to, amount);
    }
  }

  function _mintToken(address token, address to, uint256 amount) internal {
    IERC20Mintable(token).mint(to, amount);
  }

  function getStakeInfo(
    uint256 poolId,
    address account,
    uint256 index
  ) external view returns (StakingInfo memory) {
    return stakeInfo[poolId][account][index];
  }

  function getStakedTimes(
    uint256 poolId,
    address account
  ) external view returns (uint256) {
    return stakeInfo[poolId][account].length;
  }

  function stake(uint256 poolId, uint256 amount) external payable {
    LockedPoolInfo memory pool = poolInfo[poolId];
    if (pool.timelock == 0 || pool.enabled == false) revert PoolClosed(poolId);

    uint256 yieldAmount = (amount * pool.yieldAPY * pool.timelock) /
      YIELD_DENOM /
      365 /
      86400;

    StakingInfo memory staking = StakingInfo(
      amount,
      block.timestamp + pool.timelock,
      yieldAmount
    );
    stakeInfo[poolId][msg.sender].push(staking);
    poolInfo[poolId].totalStaked += amount;

    _depositToken(pool.stakeToken, msg.sender, amount);
    _mintToken(pool.yieldToken, msg.sender, yieldAmount);

    emit Stake(
      poolId,
      pool.timelock,
      pool.yieldAPY,
      pool.yieldToken,
      pool.stakeToken,
      amount
    );
  }

  function unstake(uint256 poolId) external {
    LockedPoolInfo memory pool = poolInfo[poolId];
    if (pool.timelock == 0) revert PoolNotExisted(poolId);

    StakingInfo[] storage stakes = stakeInfo[poolId][msg.sender];
    if (stakes.length == 0) revert NoStaking();

    uint256 totalStaked = 0;
    for (int256 i = int256(stakes.length) - 1; i >= 0; i--) {
      StakingInfo memory staked = stakes[uint256(i)];

      if (staked.unlockTime <= block.timestamp) {
        totalStaked += staked.amount;
        // delete by stake time
        stakes[uint256(i)] = stakes[stakes.length - 1];
        stakes.pop();
      }
    }

    poolInfo[poolId].totalStaked -= totalStaked;

    if (totalStaked == 0) revert NoStaking();

    _transferToken(pool.stakeToken, msg.sender, totalStaked);

    emit Unstake(poolId, pool.stakeToken, totalStaked);
  }
}

